// Get token from previous node
const tokenResponse = $('获取飞书Token').first().json;
if (tokenResponse.code !== 0) {
  throw new Error('获取Token失败: ' + tokenResponse.msg);
}
const token = tokenResponse.tenant_access_token;

// Get original webhook data
const input = $('Webhook 接收记账数据').first().json;

// Support multiple input formats
let record = {};
if (input.body) {
  record = input.body;
} else {
  record = input;
}

// Validate required fields
const amount = parseFloat(record.amount);
if (isNaN(amount)) {
  throw new Error('Invalid amount: ' + record.amount);
}

// Build standardized record
const now = new Date();
const result = {
  token: token,
  date: record.date || now.toISOString().split('T')[0],
  amount: amount,
  type: amount >= 0 ? '收入' : '支出',
  category: record.category || '其他',
  remark: record.remark || record.note || '',
  created_at: now.toISOString()
};

return [{ json: result }];

