const feishuResponse = $('写入飞书多维表格').first().json;
const recordData = $('解析记账数据').first().json;

if (feishuResponse.code === 0) {
  return [{
    json: {
      success: true,
      message: '记账成功',
      data: {
        date: recordData.date,
        amount: recordData.amount,
        category: recordData.category,
        record_id: feishuResponse.data?.record?.record_id
      }
    }
  }];
} else {
  return [{
    json: {
      success: false,
      message: '记账失败',
      error: feishuResponse.msg || '未知错误'
    }
  }];
}
